mapper.select:
select drm.menu_id from t_role_menu drm left join t_menu dm on drm.menu_id = dm.id
where drm.role_id = #{roleId,jdbcType=BIGINT}
order by drm.menu_id

mapper.insert:
insert into t_role_menu (role_id, menu_id, create_user, create_time)
values (
#{roleId,jdbcType=INTEGER},#{menuId,jdbcType=BIGINT},#{createUser,jdbcType=VARCHAR},
#{createTime,jdbcType=TIMESTAMP}
)
